from pwn import *
import gmpy2
import sympy

r = remote('challs.xmas.htsp.ro', 10005)
menu = r.recv()
menu = menu.decode()
#print(menu)

r.sendline('1337')
cipher = r.recvline()
cipher = int(cipher.decode().split(' ')[-1])
#cipher = 
r.recv()

high = gmpy2.mpz(10**309)
low = gmpy2.mpz(10**308)
while high > low:
    mid = (high+low) // 2
    r.sendline('2')
    r.recv()
    r.sendline(str(mid))
    tmp = r.recvline()
    r.recv()

    tmp = tmp.decode()
    tmp = tmp.split(' ')
    encypt = int(tmp[-1])

    if gmpy2.square(mid) == encypt:
        low = mid
    else:
        high = mid

print('cipher: ', cipher)
print('high: ', high)

r.close()
